mapper.update:
UPDATE ACT_RU_RESOURCE SET IS_ACTIVE_ = #{parameter.active},
TIME_ACTIVE_ = TIME_ACTIVE_ + TIMESTAMPDIFF('MS', LAST_CHANGE_, CURRENT_TIMESTAMP), TIME_IDLE_ = (CASE IS_ASSIGNED_ WHEN TRUE THEN TIME_IDLE_ ELSE TIME_IDLE_ + TIMESTAMPDIFF('MS', LAST_CHANGE_, CURRENT_TIMESTAMP) END),
LAST_CHANGE_ = CURRENT_TIMESTAMP WHERE ID_ = #{parameter.id}
